import {Dialog, Box, Typography, List, ListItem} from '@mui/material';
import { qrCodeImage } from '../constants/data';
const  dialogStyle = {
    height : `96%`,
    marginTop: '12%',
    width: '60%',
    maxWidth: '100%',
    maxHeight: '100%',
    boxShadow: 'none',
    overflow: 'none'
}

const LoginDialog = () =>{
    return(
        <Dialog 
            open={true}
            PaperProps = {{sx : dialogStyle}} 
            >
            <Box>
                <Box>
                 <Typography>To use LetsChat on your Computer : </Typography>
                 <List>
                    <ListItem>1. Open LetsChat on your phone</ListItem>
                    <ListItem>2. Tap Menu Settingsw and select LetsChat Web</ListItem>
                    <ListItem>3. Point your phone to this screen to capture the code</ListItem>
                 </List>

                </Box>
                   <img src={qrCodeImage} alt="qr code" />
                <Box>

                </Box>
            </Box>

        </Dialog>
    )
}

export default LoginDialog;
